import React, { useState } from 'react';
import Head from 'next/head';
import { AppProps } from 'next/app';



class Modal extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      counters: 0,
      create: "thanh"
    }
  }
  render() {
    return (
        <div>
        {/* Hello world */}
        <div className="awesome" style={{border: '1px solid red'}}>
          <label htmlFor="name">Enter your name: </label>
          <input type="text" id="name" />
          <h2>Modal Login Example</h2>
          {/* Trigger the modal with a button */}
          <button type="button" className="btn btn-default btn-lg" id="myBtn">Login</button>
          {/* Modal */}
          <div className="modal fade" id="myModal" role="dialog">
            <div className="modal-dialog">
              {/* Modal content*/}
              <div className="modal-content">
                <div className="modal-header">
                  <button type="button" className="close" data-dismiss="modal">Ã—</button>
                  <h4 style={{color: 'red'}}><span className="glyphicon glyphicon-lock" /> Login</h4>
                </div>
                <div className="modal-body">
                  <form role="form">
                    <div className="form-group">
                      <label htmlFor="usrname"><span className="glyphicon glyphicon-user" /> Username</label>
                      <input type="text" className="form-control" id="usrname" placeholder="Enter email" />
                    </div>
                    <div className="form-group">
                      <label htmlFor="psw"><span className="glyphicon glyphicon-eye-open" /> Password</label>
                      <input type="text" className="form-control" id="psw" placeholder="Enter password" />
                    </div>
                    <div className="checkbox">
                      <label><input type="checkbox" defaultValue defaultChecked />Remember me</label>
                    </div>
                    <button type="submit" className="btn btn-default btn-success btn-block"><span className="glyphicon glyphicon-off" /> Login</button>
                  </form>
                </div>
                <div className="modal-footer">
                  <button type="submit" className="btn btn-default btn-default pull-left" data-dismiss="modal"><span className="glyphicon glyphicon-remove" /> Cancel</button>
                  <p>Not a member? <a href="#">Sign Up</a></p>
                  <p>Forgot <a href="#">Password?</a></p>
                </div>
              </div>
            </div>
          </div> 
        </div>
        <p>Enter your HTML here</p>
      </div>
      
      
    )
  }
}
export default Modal;
