import fetch from 'isomorphic-unfetch';

module.exports = {
    Send_Post_RestAPI: function(url){
        return new Promise((resove, reject)=>{
            const res = fetch(`${url}`,{
                method: 'GET',
                mode: 'cors',
                headers: {
                  'Content-Type': 'application/json; charset=utf-8',
                  'Accept': 'application/json',
                },
                body: JSON.stringify({operationName:null, variables:{}})
              });
            //reject('error');
            resove(res);
        });
    },
}
